function validateUsername(username) {
    // Check length
    if (username.length < 2 || username.length > 30) {
        return { valid: false, message: "Username must be 2 to 30 characters long." };
    }

    // Check character set
    const regex = /^[a-zA-Z0-9_]+$/;
    if (!regex.test(username)) {
        return { valid: false, message: "Username can only contain letters, numbers, and underscores." };
    }

    // Prevent leading or trailing underscores (optional)
    if (username.startsWith('_') || username.endsWith('_')) {
        return { valid: false, message: "Username cannot start or end with an underscore." };
    }

    // Sanitize input
    const sanitized = username.replace(/[^a-zA-Z0-9_]/g, '');
    if (sanitized !== username) {
        return { valid: false, message: "Username contains invalid characters." };
    }

    return { valid: true, username: sanitized };
}
